import React, { useEffect, useState } from "react";
import "./index.css"
import Header from "./Components/Header.js"
import InputCity from "./Components/InputCity.js"
const API_KEY = process.env.WEATHER_KEY;
console.log(`api key: ${API_KEY}`)
import axios from "axios";

const App = () => {
    const [inputCity, setInputCity] = useState("Seattle");
    const [cityName, setCityName] = useState("Seattle");
    let lon = "";
    console.log(lon)
    let lat = ""; 
    console.log(lat)
    async function getLatAndLon() {
        try {
            const response = await axios.get(`https://api.openweathermap.org/geo/1.0/direct?q=${cityName}&limit=1&appid=${API_KEY}`);
            const {lat, lon} = response.data;
            console.log(`lat long :${lat} ${lon}`)
        } catch (error) {
            console.log(`${error} error getting lat and lon`)
        }
    }

