        // fetch(longLatUrl).then(response => response.json()).then(data =>  {
        //     console.log(data)
        //     lon = data.lon;
        //     lat = data.lat;
        //     console.log(lat)
        // }).catch(e => console.log(e ))

    }, [cityName])

    //  Input element handler
    const inputHandler = (e) => {
        setInputCity(e.target.value);
    };

    //  Search button
    const submitHandler = (e) => {
        e.preventDefault();
        // setError(false);
        setCityName(inputCity);
    };

    return (
        <div>
            <Header />
            <InputCity
                city={inputCity}
                onInputHandler={inputHandler}
                onSubmitHandler={submitHandler}
            />
        </div>
    )
}

export default App
